        children: function(idx) {
            var distanceX = 200;
            var distanceY = 170;
            var config = {
                scale: 0.2,
                y: -1,
                x: ((idx%5) - 2) * (1/2),
                rotateY: 360,
                secondary: {
                    "": "self",
